import{_ as t,o as e,c as d,N as l}from"./app.87c25b9d.js";import"./chunks/theme.05c5a606.js";const y=JSON.parse('{"title":"电脑系统相关整理","description":"","frontmatter":{},"headers":[{"level":2,"title":"查看 IP 地址","slug":"查看-ip-地址","link":"#查看-ip-地址","children":[]},{"level":2,"title":"截图快捷键","slug":"截图快捷键","link":"#截图快捷键","children":[]},{"level":2,"title":"本地 IIS 服务开启与启动","slug":"本地-iis-服务开启与启动","link":"#本地-iis-服务开启与启动","children":[]},{"level":2,"title":"windows 配置本地 hosts","slug":"windows-配置本地-hosts","link":"#windows-配置本地-hosts","children":[]},{"level":2,"title":"Win10 快捷键","slug":"win10-快捷键","link":"#win10-快捷键","children":[]},{"level":2,"title":"Mac 快捷键","slug":"mac-快捷键","link":"#mac-快捷键","children":[]},{"level":2,"title":"Mac 特殊符号","slug":"mac-特殊符号","link":"#mac-特殊符号","children":[{"level":4,"title":"数学相关","slug":"数学相关","link":"#数学相关","children":[]},{"level":4,"title":"单位相关","slug":"单位相关","link":"#单位相关","children":[]},{"level":4,"title":"货币单位","slug":"货币单位","link":"#货币单位","children":[]},{"level":4,"title":"商标相关","slug":"商标相关","link":"#商标相关","children":[]},{"level":4,"title":"符号相关","slug":"符号相关","link":"#符号相关","children":[]},{"level":4,"title":"其他常用符号","slug":"其他常用符号","link":"#其他常用符号","children":[]}]},{"level":2,"title":"输入法高效使用技巧","slug":"输入法高效使用技巧","link":"#输入法高效使用技巧","children":[]}],"relativePath":"blog/others/organize-computer-system.md"}'),i={name:"blog/others/organize-computer-system.md"},n=l(`<h1 id="电脑系统相关整理" tabindex="-1">电脑系统相关整理 <a class="header-anchor" href="#电脑系统相关整理" aria-hidden="true">#</a></h1><h2 id="查看-ip-地址" tabindex="-1">查看 IP 地址 <a class="header-anchor" href="#查看-ip-地址" aria-hidden="true">#</a></h2><ul><li><code>window</code>中在 cmd 中输入</li></ul><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">$</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ipconfig</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><ul><li><code>mac</code> 中在 cmd 中输入</li></ul><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">$</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ifconfig</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="截图快捷键" tabindex="-1">截图快捷键 <a class="header-anchor" href="#截图快捷键" aria-hidden="true">#</a></h2><ul><li><code>Print Screen</code>截取全屏并保存在剪切板，在编辑器、文件夹、聊天窗口粘贴使用；（适合应用在截取右键菜单、快捷键导致鼠标菜单失效情况）</li><li><code>Win + Print Scree</code>截取全屏并自动保存到 <strong>&quot;此电脑&gt;视频&gt;捕获&quot;</strong> 目录下；</li><li><code>Win + Shift + S</code>调取系统截图工具，在编辑器、文件夹、聊天窗口粘贴使用；</li></ul><h2 id="本地-iis-服务开启与启动" tabindex="-1">本地 IIS 服务开启与启动 <a class="header-anchor" href="#本地-iis-服务开启与启动" aria-hidden="true">#</a></h2><ul><li><a href="https://www.cnblogs.com/teyigou/p/8125379.html" target="_blank" rel="noreferrer">https://www.cnblogs.com/teyigou/p/8125379.html</a></li><li>打开IIS管理器， win + R 输入 inetmgr</li><li>本地访问地址：<code>http://localhost:8099/</code>、<code>http://172.16.9.209:8099/</code></li><li><a href="https://jingyan.baidu.com/article/fb48e8be39ca736e622e1427.html" target="_blank" rel="noreferrer">iis配置，其他电脑无法访问</a></li></ul><h2 id="windows-配置本地-hosts" tabindex="-1">windows 配置本地 hosts <a class="header-anchor" href="#windows-配置本地-hosts" aria-hidden="true">#</a></h2><ul><li><code>Win+R</code>打开运行框，然后键入<code>hosts</code>文件路径：<code>C:\\WINDOWS\\system32\\drivers\\etc</code></li><li><a href="https://blog.csdn.net/weixin_58942821/article/details/120669937" target="_blank" rel="noreferrer">windows配置本地hosts</a></li></ul><h2 id="win10-快捷键" tabindex="-1">Win10 快捷键 <a class="header-anchor" href="#win10-快捷键" aria-hidden="true">#</a></h2><blockquote><p>参考资料：<a href="https://support.microsoft.com/zh-cn/windows/windows-%E7%9A%84%E9%94%AE%E7%9B%98%E5%BF%AB%E6%8D%B7%E6%96%B9%E5%BC%8F-dcc61a57-8ff0-cffe-9796-cb9706c75eec" target="_blank" rel="noreferrer">【官方】Windows 的键盘快捷方式</a></p></blockquote><table><thead><tr><th style="text-align:left;">功能</th><th style="text-align:left;">快捷键</th></tr></thead><tbody><tr><td style="text-align:left;">Win10 颜色筛选器功能，黑白色切换</td><td style="text-align:left;"><kbd>Windows</kbd> + <kbd>Ctrl</kbd> + <kbd>C</kbd></td></tr><tr><td style="text-align:left;">电脑桌面关机/退出应用</td><td style="text-align:left;"><kbd>Alt</kbd> + <kbd>F4</kbd>（+ <kbd>Fn</kbd>）</td></tr><tr><td style="text-align:left;">鼠标右键</td><td style="text-align:left;"><kbd>Shift</kbd> + <kbd>F10</kbd>（+ <kbd>Fn</kbd>）</td></tr><tr><td style="text-align:left;">永久删除所选项</td><td style="text-align:left;"><kbd>Shift</kbd> + <kbd>Delete</kbd></td></tr><tr><td style="text-align:left;">电脑快速重新启动</td><td style="text-align:left;"><kbd>Ctrl</kbd> + <kbd>Alt</kbd> + <kbd>Delete</kbd></td></tr><tr><td style="text-align:left;">打开任务管理器</td><td style="text-align:left;"><kbd>Ctrl</kbd> + <kbd>Shift</kbd> + <kbd>Esc</kbd></td></tr><tr><td style="text-align:left;">电脑锁屏</td><td style="text-align:left;"><kbd>Windows</kbd> + <kbd>L</kbd></td></tr><tr><td style="text-align:left;">电脑打开“我的电脑”</td><td style="text-align:left;"><kbd>Windows</kbd> + <kbd>E</kbd></td></tr><tr><td style="text-align:left;">返回电脑桌面</td><td style="text-align:left;"><kbd>Windows</kbd> + <kbd>D</kbd></td></tr><tr><td style="text-align:left;">电脑窗口切换</td><td style="text-align:left;"><kbd>Windows</kbd> + <kbd>Tab</kbd></td></tr><tr><td style="text-align:left;">电脑快捷视图</td><td style="text-align:left;"><kbd>Windows</kbd> + <kbd>X</kbd></td></tr><tr><td style="text-align:left;">电脑放大镜功能</td><td style="text-align:left;"><kbd>Windows</kbd> + <kbd>+</kbd></td></tr><tr><td style="text-align:left;">打开录像</td><td style="text-align:left;"><kbd>Windows</kbd> + <kbd>R</kbd>，输入 psr.exe</td></tr><tr><td style="text-align:left;">打开虚拟键盘</td><td style="text-align:left;"><kbd>Windows</kbd> + <kbd>R</kbd>，输入 osk</td></tr><tr><td style="text-align:left;">查看系统版本</td><td style="text-align:left;"><kbd>Windows</kbd> + <kbd>R</kbd>，输入 winver</td></tr></tbody></table><h2 id="mac-快捷键" tabindex="-1">Mac 快捷键 <a class="header-anchor" href="#mac-快捷键" aria-hidden="true">#</a></h2><blockquote><p>参考资料：<a href="https://support.apple.com/zh-cn/HT201236" target="_blank" rel="noreferrer">【官方】Mac 键盘快捷键</a> 、<a href="https://support.apple.com/zh-cn/guide/mac-help/welcome/mac" target="_blank" rel="noreferrer">《macOS 使用手册》</a></p></blockquote><table><thead><tr><th style="text-align:left;">功能</th><th style="text-align:left;">快捷键</th></tr></thead><tbody><tr><td style="text-align:left;">查找文稿中的项目或打开“查找”窗口</td><td style="text-align:left;"><kbd> ⌘ </kbd> + <kbd>F</kbd></td></tr><tr><td style="text-align:left;">从“访达”窗口进行“聚焦”搜索 （同上）</td><td style="text-align:left;"><kbd> ⌘ </kbd> + <kbd>Option</kbd> + <kbd>空格</kbd></td></tr><tr><td style="text-align:left;">隐藏最前面的应用的窗口</td><td style="text-align:left;"><kbd> ⌘ </kbd> + <kbd>H</kbd></td></tr><tr><td style="text-align:left;">将最前面的窗口最小化至“程序坞”</td><td style="text-align:left;"><kbd> ⌘ </kbd> + <kbd>M</kbd></td></tr><tr><td style="text-align:left;">打开所选项</td><td style="text-align:left;"><kbd> ⌘ </kbd> + <kbd>O</kbd></td></tr><tr><td style="text-align:left;">打开新标签页</td><td style="text-align:left;"><kbd> ⌘ </kbd> + <kbd>T</kbd></td></tr><tr><td style="text-align:left;">关闭最前面的窗口</td><td style="text-align:left;"><kbd> ⌘ </kbd> + <kbd>W</kbd></td></tr><tr><td style="text-align:left;">关闭应用的所有窗口</td><td style="text-align:left;"><kbd>Option</kbd> + <kbd> ⌘ </kbd> + <kbd>W</kbd></td></tr><tr><td style="text-align:left;">显示或隐藏“聚焦”搜索栏</td><td style="text-align:left;"><kbd> ⌘ </kbd> + <kbd>空格</kbd></td></tr><tr><td style="text-align:left;">在“访达”中创建一个新文件夹</td><td style="text-align:left;"><kbd>Shift</kbd> + <kbd> ⌘ </kbd> + <kbd>N</kbd></td></tr><tr><td style="text-align:left;">全屏使用应用</td><td style="text-align:left;"><kbd>Control</kbd> + <kbd> ⌘ </kbd> + <kbd>F</kbd></td></tr><tr><td style="text-align:left;">立即锁定屏幕</td><td style="text-align:left;"><kbd>Control</kbd> + <kbd> ⌘ </kbd> + <kbd>Q</kbd></td></tr><tr><td style="text-align:left;">睡眠</td><td style="text-align:left;"><kbd>Option</kbd> + <kbd> ⌘ </kbd> + <kbd>电源按钮</kbd></td></tr><tr><td style="text-align:left;">睡眠</td><td style="text-align:left;"><kbd>Control</kbd> + <kbd>Shift</kbd> + <kbd>电源按钮</kbd></td></tr><tr><td style="text-align:left;">显示一个对话框，询问您是要重新启动、睡眠还是关机</td><td style="text-align:left;"><kbd>Control</kbd> + <kbd>电源按钮</kbd></td></tr><tr><td style="text-align:left;">切换隐藏文件</td><td style="text-align:left;"><kbd> ⌘ </kbd> + <kbd>Shift</kbd> + <kbd>.</kbd></td></tr><tr><td style="text-align:left;">切换输入法</td><td style="text-align:left;"><kbd>Control</kbd> + <kbd>空格</kbd></td></tr><tr><td style="text-align:left;">字符检视器</td><td style="text-align:left;"><kbd>Control</kbd> + <kbd> ⌘ </kbd> + <kbd>空格</kbd></td></tr></tbody></table><h2 id="mac-特殊符号" tabindex="-1">Mac 特殊符号 <a class="header-anchor" href="#mac-特殊符号" aria-hidden="true">#</a></h2><blockquote><p>参考资料：<a href="https://www.jianshu.com/p/12d46f7095ac" target="_blank" rel="noreferrer">Mac 下键盘特殊符号的那些事</a></p></blockquote><div class="tip custom-block"><p class="custom-block-title">说明</p><p>option 的意思是选择，所以在 Mac 给 option 多一种选择，当你在 Mac 下按下 option 时，总会有多一个选项，而这就是 option 的相关性。</p><ul><li><span style="color:red;">option-按键 或者 shift-option-按键 来输入。</span></li><li><span style="color:red;">使用“字符检视器”。</span>可参考：<a href="https://support.apple.com/zh-cn/guide/mac-help/mchlp1560/11.0/mac/11.0" target="_blank" rel="noreferrer">在 Mac 上使用表情和符号</a><ul><li>在 Mac 上的 App 中，选取“编辑”&gt;“表情与符号”,快捷键<kbd>Control</kbd> + <kbd> ⌘ </kbd> + <kbd>空格</kbd>；</li><li>从输入法菜单中打开“表情与符号”（如果在“键盘”偏好设置中设定了该选项）。</li></ul></li></ul></div><h4 id="数学相关" tabindex="-1">数学相关 <a class="header-anchor" href="#数学相关" aria-hidden="true">#</a></h4><table><thead><tr><th style="text-align:center;">符号</th><th>组合键</th><th style="text-align:center;">符号</th><th>组合键</th></tr></thead><tbody><tr><td style="text-align:center;">÷</td><td>option + /</td><td style="text-align:center;">≠</td><td>option + =</td></tr><tr><td style="text-align:center;">≥</td><td>option + &gt;</td><td style="text-align:center;">≤</td><td>option + &lt;</td></tr><tr><td style="text-align:center;">≈</td><td>option + x</td><td style="text-align:center;">±</td><td>shift + option + +</td></tr><tr><td style="text-align:center;">⁄（分号）</td><td>shift + option + 1</td><td style="text-align:center;">‰（千分号）</td><td>shift + option + R</td></tr><tr><td style="text-align:center;">√（平方根）</td><td>option + V</td><td style="text-align:center;">￢（逻辑非）</td><td>option + L</td></tr><tr><td style="text-align:center;">π（pi）</td><td>option + P</td><td style="text-align:center;">∏（元运算）</td><td>shift + option + P</td></tr><tr><td style="text-align:center;">∞（无穷大）</td><td>option + 5</td><td style="text-align:center;">ø（空集）</td><td>option + O</td></tr><tr><td style="text-align:center;">∑（求和）</td><td>option + W</td><td style="text-align:center;">ƒ（函数）</td><td>option + F</td></tr><tr><td style="text-align:center;">∫（integral 积分）</td><td>option + B</td><td style="text-align:center;">∂（partial differential 偏微分）</td><td>option + D</td></tr><tr><td style="text-align:center;">∆（变量）</td><td>option + J</td><td style="text-align:center;">-</td><td>-</td></tr></tbody></table><h4 id="单位相关" tabindex="-1">单位相关 <a class="header-anchor" href="#单位相关" aria-hidden="true">#</a></h4><table><thead><tr><th style="text-align:center;">符号</th><th>组合键</th><th style="text-align:center;">符号</th><th>组合键</th></tr></thead><tbody><tr><td style="text-align:center;">˚（角度）</td><td>option + K</td><td style="text-align:center;">°（温度，如 ℃、℉）</td><td>shift + option + 8</td></tr></tbody></table><h4 id="货币单位" tabindex="-1">货币单位 <a class="header-anchor" href="#货币单位" aria-hidden="true">#</a></h4><table><thead><tr><th style="text-align:center;">符号</th><th>组合键</th><th style="text-align:center;">符号</th><th>组合键</th></tr></thead><tbody><tr><td style="text-align:center;">¢（美分）</td><td>option + 4</td><td style="text-align:center;">￡（英镑）</td><td>option + 3</td></tr><tr><td style="text-align:center;">€（欧元）</td><td>shift + option + 2</td><td style="text-align:center;">¥（元）</td><td>option + Y</td></tr></tbody></table><h4 id="商标相关" tabindex="-1">商标相关 <a class="header-anchor" href="#商标相关" aria-hidden="true">#</a></h4><table><thead><tr><th style="text-align:center;">符号</th><th>组合键</th><th style="text-align:center;">符号</th><th>组合键</th></tr></thead><tbody><tr><td style="text-align:center;">®（注册商标）</td><td>option + R</td><td style="text-align:center;">©（版权）</td><td>option + G</td></tr><tr><td style="text-align:center;">™（商标）</td><td>option + 2</td><td style="text-align:center;">-</td><td>-</td></tr></tbody></table><h4 id="符号相关" tabindex="-1">符号相关 <a class="header-anchor" href="#符号相关" aria-hidden="true">#</a></h4><table><thead><tr><th style="text-align:center;">符号</th><th>组合键</th><th style="text-align:center;">符号</th><th>组合键</th></tr></thead><tbody><tr><td style="text-align:center;">…（水平省略号）</td><td>option + :</td><td style="text-align:center;">¡（反感叹号）</td><td>option + 1</td></tr><tr><td style="text-align:center;">¿（反问号）</td><td>shift + option + ?</td><td style="text-align:center;">§（分节符）</td><td>option + 6</td></tr><tr><td style="text-align:center;">¶（分隔符）</td><td>option + 7</td><td style="text-align:center;">-</td><td>-</td></tr></tbody></table><h4 id="其他常用符号" tabindex="-1">其他常用符号 <a class="header-anchor" href="#其他常用符号" aria-hidden="true">#</a></h4><table><thead><tr><th style="text-align:center;">符号</th><th>组合键</th></tr></thead><tbody><tr><td style="text-align:center;">（Apple Logo）</td><td>shift + option + K</td></tr><tr><td style="text-align:center;">·（中点，英文名中常用的点）</td><td>shift + option + 9</td></tr></tbody></table><h2 id="输入法高效使用技巧" tabindex="-1">输入法高效使用技巧 <a class="header-anchor" href="#输入法高效使用技巧" aria-hidden="true">#</a></h2><ol><li>敲击 tab 键+部首，快速找到生僻字；</li><li>活用键盘符号； <ul><li>xi&#39;an (中文单引号)</li><li>Ctrl + 首字母</li></ul></li><li>启用 <code>u 模式</code>，快速找到生僻字； <ul><li>中文输入： ushiwei 、uniuniuniu</li></ul></li><li>启用 <code>v 模式</code>，秒变计算器;</li><li>启用简拼；</li></ol><blockquote><p>参考资料：<a href="https://www.bilibili.com/video/BV1754y1J7ma?spm_id_from=333.851.b_7265636f6d6d656e64.1" target="_blank" rel="noreferrer">【B 站最全】8 个输入法高效使用技巧，大幅提升你的打字效率，你知道几个？</a></p></blockquote>`,36),a=[n];function r(s,o,b,h,c,k){return e(),d("div",null,a)}const x=t(i,[["render",r]]);export{y as __pageData,x as default};
