import{_ as e,o as l,c as r,N as i}from"./app.87c25b9d.js";import"./chunks/theme.05c5a606.js";const p=JSON.parse('{"title":"魔方公式","description":"","frontmatter":{},"headers":[{"level":3,"title":"1、拼底层十字","slug":"_1、拼底层十字","link":"#_1、拼底层十字","children":[]},{"level":3,"title":"2、拼底层角块","slug":"_2、拼底层角块","link":"#_2、拼底层角块","children":[]},{"level":3,"title":"3、还原第二层","slug":"_3、还原第二层","link":"#_3、还原第二层","children":[]},{"level":3,"title":"4、拼顶面十字","slug":"_4、拼顶面十字","link":"#_4、拼顶面十字","children":[]},{"level":3,"title":"5、还原顶面颜色","slug":"_5、还原顶面颜色","link":"#_5、还原顶面颜色","children":[]},{"level":3,"title":"6、拼顶面对角块","slug":"_6、拼顶面对角块","link":"#_6、拼顶面对角块","children":[]},{"level":3,"title":"7、调整顶层棱块","slug":"_7、调整顶层棱块","link":"#_7、调整顶层棱块","children":[]}],"relativePath":"blog/others/rubik-cube-formula.md"}'),t={name:"blog/others/rubik-cube-formula.md"},a=i('<h1 id="魔方公式" tabindex="-1">魔方公式 <a class="header-anchor" href="#魔方公式" aria-hidden="true">#</a></h1><blockquote><p>参考资料：<a href="https://www.bilibili.com/video/BV1hJ411p7k6?p=2" target="_blank" rel="noreferrer">零基础学会魔方！超详细的三阶魔方入门还原教学！</a></p></blockquote><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><strong>右手公式：上左下右 左手公式：上右下左</strong></p></div><h3 id="_1、拼底层十字" tabindex="-1">1、拼底层十字 <a class="header-anchor" href="#_1、拼底层十字" aria-hidden="true">#</a></h3><h3 id="_2、拼底层角块" tabindex="-1">2、拼底层角块 <a class="header-anchor" href="#_2、拼底层角块" aria-hidden="true">#</a></h3><ul><li><strong>上左下右</strong></li></ul><h3 id="_3、还原第二层" tabindex="-1">3、还原第二层 <a class="header-anchor" href="#_3、还原第二层" aria-hidden="true">#</a></h3><ul><li>顶层目标棱块对好颜色</li><li>情况1：去左边空档：棱块往右远离→<strong>上右下左</strong>→空档移到右手→<strong>上左下右</strong></li><li>情况2：去右边空档：棱块往左远离→<strong>上左下右</strong>→空档移到左手→<strong>上右下左</strong></li></ul><h3 id="_4、拼顶面十字" tabindex="-1">4、拼顶面十字 <a class="header-anchor" href="#_4、拼顶面十字" aria-hidden="true">#</a></h3><ul><li><strong>前面顺时针，上左下右，前面逆时针</strong></li></ul><h3 id="_5、还原顶面颜色" tabindex="-1">5、还原顶面颜色 <a class="header-anchor" href="#_5、还原顶面颜色" aria-hidden="true">#</a></h3><ul><li>拼小鱼： <ul><li>情况1：上面缺两点黄色，左后方放黄色 做公式：<strong>下右上右下右右上</strong></li><li>情况2：上面缺四点黄色，后面不能出现黄色 做公式：<strong>下右上右下右右上</strong></li></ul></li><li>还原顶面黄色： <ul><li>小鱼鱼头放在左上角 做公式：<strong>下右上右下右右上</strong></li></ul></li></ul><h3 id="_6、拼顶面对角块" tabindex="-1">6、拼顶面对角块 <a class="header-anchor" href="#_6、拼顶面对角块" aria-hidden="true">#</a></h3><ul><li>黄色面对着自己，好的一组放下面 做公式：<strong>上右上，下180°，下左上，下180°，右180°</strong></li></ul><h3 id="_7、调整顶层棱块" tabindex="-1">7、调整顶层棱块 <a class="header-anchor" href="#_7、调整顶层棱块" aria-hidden="true">#</a></h3><ul><li>还原的侧面对着自己 做公式：<strong>下左下右，下右下左，上左，右180°</strong></li></ul>',16),n=[a];function s(o,h,d,_,c,u){return l(),r("div",null,n)}const f=e(t,[["render",s]]);export{p as __pageData,f as default};
