import{_ as e,o,c as d,N as t}from"./app.87c25b9d.js";import"./chunks/theme.05c5a606.js";const m=JSON.parse('{"title":"VSCode 常见问题","description":"","frontmatter":{},"headers":[{"level":2,"title":"001_完美解决： VSCode 中文显示","slug":"_001-完美解决-vscode-中文显示","link":"#_001-完美解决-vscode-中文显示","children":[]},{"level":2,"title":"002_VSCode 中保存后会在当前目录下自动生成 dist 目录","slug":"_002-vscode-中保存后会在当前目录下自动生成-dist-目录","link":"#_002-vscode-中保存后会在当前目录下自动生成-dist-目录","children":[]},{"level":2,"title":"003_VSCode 无法更改文件夹名称","slug":"_003-vscode-无法更改文件夹名称","link":"#_003-vscode-无法更改文件夹名称","children":[]},{"level":2,"title":"004_卸载删除干净","slug":"_004-卸载删除干净","link":"#_004-卸载删除干净","children":[]}],"relativePath":"blog/plugin/vscode-QA.md"}'),c={name:"blog/plugin/vscode-QA.md"},s=t('<h1 id="vscode-常见问题" tabindex="-1">VSCode 常见问题 <a class="header-anchor" href="#vscode-常见问题" aria-hidden="true">#</a></h1><h2 id="_001-完美解决-vscode-中文显示" tabindex="-1">001_完美解决： VSCode 中文显示 <a class="header-anchor" href="#_001-完美解决-vscode-中文显示" aria-hidden="true">#</a></h2><ul><li><p>第一步：找到 <strong><code>扩展</code></strong>，搜索 <strong><code>chinese</code></strong> 或者 <a href="https://marketplace.visualstudio.com/items?itemName=MS-CEINTL.vscode-language-pack-zh-hans" target="_blank" rel="noreferrer">访问</a>，点击 Install 进行下载，然后重启 VSCode；</p></li><li><p>第二步：<kbd>Ctrl</kbd>+<kbd>Shift</kbd>+<kbd>P</kbd> 或者 <kbd> ⌘ </kbd>+<kbd>Shift</kbd>+<kbd>P</kbd>，打开命令行，搜索 <strong><code>language</code></strong>，选择 <strong><code>Configure Display Language</code></strong>，再选择 <strong><code>zh-cn</code></strong> 即可。</p><blockquote><p>参考资料：<a href="https://blog.csdn.net/qq_24118527/article/details/82793610" target="_blank" rel="noreferrer">完美解决：VSCode 中文显示</a></p></blockquote></li></ul><h2 id="_002-vscode-中保存后会在当前目录下自动生成-dist-目录" tabindex="-1">002_VSCode 中保存后会在当前目录下自动生成 dist 目录 <a class="header-anchor" href="#_002-vscode-中保存后会在当前目录下自动生成-dist-目录" aria-hidden="true">#</a></h2><p><img src="https://z3.ax1x.com/2021/08/11/fNfADU.png" alt=""></p><div class="danger custom-block"><p class="custom-block-title">产生原因：</p><p><a href="https://gitee.com/wscats/compile-hero/blob/master/README.CN.md" target="_blank" rel="noreferrer"><code>Sass/Less/Stylus/Pug/Jade/Typescript/Javascript Compile Hero Pro</code> 中文介绍</a></p></div><div class="tip custom-block"><p class="custom-block-title">解决办法：</p><p>关闭 <code>compile-hero</code> 插件</p><ul><li>在设置中搜索 <code>compile-hero</code> 插件</li><li>关闭所有自动生成 dist 目录的选项</li></ul></div><details class="details custom-block"><summary>如图所示</summary><p><img src="https://z3.ax1x.com/2021/08/11/fN42j0.png" alt=""><img src="https://z3.ax1x.com/2021/08/11/fNOqFP.png" alt=""></p></details><blockquote><p>参考资料：<a href="https://blog.csdn.net/weixin_38233549/article/details/108226135" target="_blank" rel="noreferrer">VsCode 中 ctrl+s 后会在当前目录下自动生成 dist 目录</a></p></blockquote><h2 id="_003-vscode-无法更改文件夹名称" tabindex="-1">003_VSCode 无法更改文件夹名称 <a class="header-anchor" href="#_003-vscode-无法更改文件夹名称" aria-hidden="true">#</a></h2><div class="tip custom-block"><p class="custom-block-title">解决办法：</p><p>删除文件内<code>node_modules</code>目录，再进行重命名操作。</p></div><h2 id="_004-卸载删除干净" tabindex="-1">004_卸载删除干净 <a class="header-anchor" href="#_004-卸载删除干净" aria-hidden="true">#</a></h2><ul><li>从控制面板找到 vscode 删除（插件和个人配置）；</li><li><code>win+R</code> 输入 <code>%userprofile%</code>，删除当前路径下的<code>.vscode</code>文件夹；</li><li><code>win+R</code> 输入 <code>%appdata%</code>，删除当前路径下的<code>code</code>和<code>vscode</code>文件夹即可。</li></ul>',13),a=[s];function l(i,r,n,p,h,_){return o(),d("div",null,a)}const b=e(c,[["render",l]]);export{m as __pageData,b as default};
